import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'

export default function Order({ firstName, lastName, items, phone, zipCode, city, email, address }) {

    var [pressed, setPressed] = useState(true);

    var touchProps = () => {
        setPressed(!pressed);
    }
    return (
        <View style={pressed ? styles.orderNormal : styles.orderHighlighted} onStartShouldSetResponder={touchProps}>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Zamówienie: {items}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Imię: {firstName}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Nazwisko: {lastName}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Email: {email}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Miasto: {city}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Numer tel: {phone}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Kod poczt: {zipCode}</Text>
            <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Ulica i numer: {address}</Text>
            <TouchableOpacity style={styles.done} onPress={() => alert('0')}>
                <Text style={pressed ? styles.txtNormal : styles.txtHighlighted}>Gotowe</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    orderNormal: {
        display: 'flex',
        backgroundColor: 'white',
        borderWidth: 1,
        borderStyle: 'solid',
        borderColor: 'black',
        borderRadius: 10,
        margin: 10,
        padding: 10,
    },
    orderHighlighted: {
        display: 'flex',
        backgroundColor: '#007bff',
        borderWidth: 1,
        borderStyle: 'solid',
        borderColor: 'black',
        borderRadius: 10,
        margin: 10,
        padding: 10,
    },
    txtNormal: {
        color: 'black',
    },
    txtHighlighted: {
        color: 'black',
    },
    done: {
        backgroundColor: '#007bff',
        borderWidth: 1,
        borderStyle: 'solid',
        borderColor: '#007bff',
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
        width: 100,
        height: 30,
        marginLeft: 300,
    }
});
