import React, { useState, Component } from 'react'
import { FlatList, SafeAreaView, StyleSheet, Text, View, TouchableOpacity, TouchableHighlight } from 'react-native'
//import database from '@react-native-firebase/database';
import * as firebase from "firebase";
import { firebaseConfig } from "./Firebase";
import Order from "./Order";
//import { render } from 'react-dom';


if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}


export default class Fetch extends Component {

  // const [dlugosc, setDlugosc] = useState();
  // const [orders, setOrders] = useState(0);
  // const [keys, setKeys] = useState(0);
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      pressed: false,
    }
    this.handleClick = this.handleClick.bind(this);
  }


  componentDidMount() {

    firebase.database()
      .ref('orders')
      .on('value', (snapshot) => {
        var li = [];
        snapshot.forEach((child) => {
          li.push({
            key: child.key,
            address: child.val().firstName,
            city: child.val().city,
            email: child.val().email,
            firstName: child.val().firstName,
            items: child.val().items,
            lastName: child.val().lastName,
            phone: child.val().phone,
            zipCode: child.val().zipCode,
          })
        })
        this.setState({ list: li })
      })
  }

  handleClick() {
    this.setState(state => ({
      pressed: !state.pressed
    }));
    this.state.pressed ? alert('true') : alert('false');
  }
  render() {
    return (
      <SafeAreaView style={styles.app}>
        <FlatList style={{ width: '100%' }}
          data={this.state.list}
          keyExtractor={(item) => item.key}
          renderItem={({ item }) => {
            return (

              <View style={this.state.pressed ? styles.orderNormal : styles.orderHighlighted} onStartShouldSetResponder={this.handleClick}>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Zamówienie: {item.items}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Imię: {item.firstName}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Nazwisko: {item.lastName}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Email: {item.email}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Miasto: {item.city}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Ulica i numer:{item.address}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Telefon: {item.phone}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Kod pocztowy: {item.zipCode}</Text>
                <TouchableOpacity style={this.state.pressed ? styles.doneNormal : styles.doneHighlighted} onPress={() => alert('0')}>
                  <Text style={this.state.pressed ? styles.txtBttnNormal : styles.txtBttnHighlighted}>Gotowe</Text>
                </TouchableOpacity>
              </View>


            )
          }}
        />
      </SafeAreaView>
    )
  }
}


const styles = StyleSheet.create({
  orderNormal: {
    display: 'flex',
    backgroundColor: 'white',
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: 'black',
    borderRadius: 10,
    margin: 10,
    padding: 20,
  },
  orderHighlighted: {
    display: 'flex',
    backgroundColor: '#007bff',
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: 'black',
    borderRadius: 10,
    margin: 10,
    padding: 20,
  },
  txtNormal: {
    color: 'blue',
    //fontWeight: 600,
  },
  txtHighlighted: {
    color: 'white',
    //fontWeight: 600,
  },
  txtBttnNormal: {
    color: 'white',
    //fontWeight: 600,
  },
  txtBttnHighlighted: {
    color: 'black',
    //fontWeight: 600,
  },
  doneNormal: {
    backgroundColor: '#007bff',
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: '#007bff',
    borderRadius: 10,
    alignItems: 'center',
    justifyContent: 'center',
    width: 100,
    height: 30,
    //marginLeft: 250,
  },
  doneHighlighted: {
    backgroundColor: 'white',
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: '#007bff',
    borderRadius: 10,
    alignItems: 'center',
    justifyContent: 'center',
    width: 100,
    height: 30,
    //marginLeft: 250,
  }
});
