import React from "react";
import { SafeAreaView, StyleSheet, Text, View } from "react-native";
import * as firebase from "firebase";
// import { render } from "react-dom";
import { firebaseConfig } from "./Firebase";
import Order from "./Order";

firebase.initializeApp(firebaseConfig);

export default class App extends React.Component {
  componentDidMount() {
    const myitems = firebase.database().ref("orders");
    myitems.on("value", snapshot => {
      // var items = [];
      var values = snapshot.val();
      // items.map((i) => (address = i.address));
      var orderId = [];
      var firstName = [];
      var lastName = [];
      var items = [];
      var phone = [];
      var zipCode = [];
      var city = [];
      var email = [];
      var address = [];
      var i = 0;
      for (const [ordId, value] of Object.entries(values)) {
        orderId = ordId;
        firstName[i] = value.firstName;
        lastName[i] = value.lastName;
        items[i] = value.items;
        phone[i] = value.phone;
        zipCode[i] = value.zipCode;
        city[i] = value.city;
        email[i] = value.email;
        address[i] = value.address;
        i++;
      }
      i = 0;


    });
    function list() {
      var columns = [];
      for (var i = 0; i < orderId.length; i++) {
        columns.push(<Order lastName={lastName[i]} />);
      }
      return columns;
    }
  }

  render() {
    return (
      <SafeAreaView style={styles.container}>
        {list}
      </SafeAreaView>
    );
  }
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  order: {
    backgroundColor: 'gray',
    borderWidth: 1,
    borderStyle: 'solid',
    borderColor: 'black',
  }
});
