import React, { createElement, useState } from 'react'
import { SafeAreaView, StyleSheet, Text, View } from 'react-native'
import database from '@react-native-firebase/database';
import * as firebase from "firebase";
import { firebaseConfig } from "./Firebase";
import Order from "./Order";

firebase.initializeApp(firebaseConfig);

const App = () => {
  const dtb = () => {
    const [col, setCol] = useState('p')
    firebase.database()
      .ref('orders')
      .on('value', gotData, errData)
  }

  function gotData(data) {
    var orders = data.val();
    var keys = Object.keys(orders);
    console.log(keys);
    for (var i = 0; i < keys.length; i++) {
      var k = keys[i];
      var firstName = orders[k].firstName;
      var items = orders[k].items;
      var li = `itemki: ${items}`;
      //li += `<Order firstName='${firstName}' items='${items}' />`;
      console.log(li);
      function Order({ firstName, lastName, items, phone, zipCode, city, email, address }) {
        return (
          <View style={styles.order}>
            <Text>Zamówienie: {items}</Text>
            <Text>Imię: {firstName}</Text>
            <Text>Nazwisko: {lastName}</Text>
            <Text>Email: {email}</Text>
            <Text>Miasto: {city}</Text>
            <Text>Numer tel: {phone}</Text>
            <Text>Kod poczt: {zipCode}</Text>
            <Text>Ulica i numer: {address}</Text>
          </View>
        )
      }
      return Order;
    }
  }

  function errData(err) {
    console.log('Error!');
    console.log(err);
  }

  const ret = () => {
    return
  }

  return (
    <SafeAreaView>
      <View>
        <ol id='yas'>
          {dtb()}
        </ol>

      </View>
    </SafeAreaView>
  )
}

export default App

