import React, { useState, Component } from 'react'
import { FlatList, SafeAreaView, StyleSheet, Text, View, TouchableOpacity, TouchableHighlight } from 'react-native'
//import database from '@react-native-firebase/database';
import * as firebase from "firebase";
import { firebaseConfig } from "./Firebase";
import Order from "./Order";
//import { render } from 'react-dom';


if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}


export default class Fetch extends Component {

  // const [dlugosc, setDlugosc] = useState();
  // const [orders, setOrders] = useState(0);
  // const [keys, setKeys] = useState(0);
  constructor(props) {
    super(props);
    this.state = {
      list: [],
      listKey: [],
      pressed: false,
      alertToggle: true,
      tempId: 0,
    }
    this.handleClick = this.handleClick.bind(this);
    this.remove = this.remove.bind(this);
  }


  componentDidMount() {

    firebase.database()
      .ref('orders')
      .on('value', (snapshot) => {
        var li = [];
        var lk = [];
        snapshot.forEach((child) => {
          lk.push({
            key: child.key,
          })
          li.push({
            key: child.key,
            id: li.length,
            address: child.val().address,
            city: child.val().city,
            email: child.val().email,
            firstName: child.val().firstName,
            items: child.val().items,
            lastName: child.val().lastName,
            phone: child.val().phone,
            zipCode: child.val().zipCode,
          })
        })
        this.setState({ list: li })
        this.setState({ listKey: lk })
      })
  }

  handleClick() {
    this.setState(state => ({
      pressed: !state.pressed
    }));
    this.state.pressed ? alert('true') : alert('false');
  }

  remove(id) {
    firebase.database()
      .ref('orders').child(this.state.listKey[id].key).remove()
    // alert(this.state.listKey)
    // console.log(this.state.listKey[id].key)
  }
  render() {
    return (
      <SafeAreaView style={styles.app}>

        <View style={this.state.alertToggle ? styles.blockHide : styles.blockShow}>
          <View style={this.state.alertToggle ? styles.alertHide : styles.alertShow}>
            <Text style={styles.alertTxt}>Czy na pewno chcesz zakończyć to zamówienie?</Text>
            <TouchableOpacity style={styles.alertBack} onPress={() => this.setState(state => ({ alertToggle: !state.alertToggle }))}>
              <Text style={styles.alertBackTxt}>Wróć</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.alertDelete} onPress={() => { this.remove(this.state.tempId); this.setState(state => ({ alertToggle: !state.alertToggle })) }}>
              <Text style={styles.alertDeleteTxt}>Usuń zamówienie</Text>
            </TouchableOpacity>
          </View>
        </View>

        <View style={styles.menu}>
          <TouchableOpacity style={styles.menuBttn} onPress={() => alert('Na razie nie działa')}>
            <Text style={styles.menuTxt}>Ustawianie ilości produktów</Text>
          </TouchableOpacity>
          <TouchableOpacity style={styles.menuBttn} onPress={() => alert('Na razie nie działa')}>
            <Text style={styles.menuTxt}>Usunięte zamówienia</Text>
          </TouchableOpacity>
        </View>
        <FlatList style={{ width: '100%' }}
          data={this.state.list}
          keyExtractor={(item) => item.key}
          renderItem={({ item }) => {
            return (




              <View style={this.state.pressed ? styles.orderNormal : styles.orderHighlighted}>



                <View style={styles.rowOrder}>
                  <Text style={styles.rowTxtOrder}>
                    Zamówienie {item.id + 1}
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.items.replace(/,/g, '\n')}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Imię:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.firstName}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Nazwisko:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.lastName}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Ulica i numer:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.address}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Kod pocztowy:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.zipCode}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Miasto:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.city}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Email:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.email}
                  </Text>
                </View>

                <View style={styles.row}>
                  <Text style={styles.rowTxt}>
                    Telefon:
                  </Text>
                  <Text style={styles.rowTxt}>
                    {item.phone}
                  </Text>
                </View>



                {/* <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Zamówienie: {item.items}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Imię: {item.firstName}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Nazwisko: {item.lastName}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Email: {item.email}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Miasto: {item.city}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Ulica i numer:{item.address}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Telefon: {item.phone}</Text>
                <Text style={this.state.pressed ? styles.txtNormal : styles.txtHighlighted}>Kod pocztowy: {item.zipCode}</Text> */}
                <View style={styles.bttn}>
                  <TouchableOpacity style={this.state.pressed ? styles.doneNormal : styles.doneHighlighted} onPress={() => { this.setState(state => ({ alertToggle: !state.alertToggle })); this.setState({ tempId: item.id }) }}>
                    <Text style={this.state.pressed ? styles.txtBttnNormal : styles.txtBttnHighlighted}>Gotowe</Text>
                  </TouchableOpacity>
                </View>

              </View>


