import React, { useState } from 'react'
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native'

export default function Order({ firstName, lastName, items, phone, zipCode, city, email, address }) {

    var [isPress, setIsPress] = useState(false);

    var touchProps = {
        style: isPress ? styles.btnPress : styles.btnNormal,
        // onHideUnderlay: () => setIsPress(false),
        // onShowUnderlay: () => setIsPress(true),
    };
    return (
        <View style={styles.order}>
            <Text style={styles.txt}>Zamówienie: {items}</Text>
            <Text style={styles.txt}>Imię: {firstName}</Text>
            <Text style={styles.txt}>Nazwisko: {lastName}</Text>
            <Text style={styles.txt}>Email: {email}</Text>
            <Text style={styles.txt}>Miasto: {city}</Text>
            <Text style={styles.txt}>Numer tel: {phone}</Text>
            <Text style={styles.txt}>Kod poczt: {zipCode}</Text>
            <Text style={styles.txt}>Ulica i numer: {address}</Text>
            <TouchableOpacity {...touchProps}>
                <Text>Gotowe</Text>
            </TouchableOpacity>
        </View>
    )
}

const styles = StyleSheet.create({
    order: {
        backgroundColor: '#403c3c',
        borderWidth: 1,
        borderStyle: 'solid',
        borderColor: 'black',
        borderRadius: 10,
        margin: 10,
        padding: 10,
    },
    txt: {
        color: 'white',
    },
    btnNormal: {
        borderColor: 'blue',
        borderWidth: 1,
        borderRadius: 10,
        height: 30,
        width: 100,
    },
    btnPress: {
        borderColor: 'blue',
        borderWidth: 1,
        height: 30,
        width: 100,
    }
});
