import React, { useState } from 'react'
import ReactDOM from 'react-dom'
import { SafeAreaView, StyleSheet, Text, View } from 'react-native'
//import database from '@react-native-firebase/database';
import * as firebase from "firebase";
import { firebaseConfig } from "./Firebase";
import Order from "./Order";
//import { render } from 'react-dom';


if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

const App = () => {

  const [dlugosc, setDlugosc] = useState();

  const dtb = () => {

    firebase.database()
      .ref('orders')
      .once('value', gotData, errData)
  }
  function gotData(data) {
    var orders = data.val();
    var keys = Object.keys(orders);
    setDlugosc(keys.length);
    console.log(keys);
    for (var i = 0; i < keys.length; i++) {
      var k = keys[i];
      var firstName = orders[k].firstName;
      var items = orders[k].items;
      var lastName = orders[k].lastName;
      var email = orders[k].email;
      var city = orders[k].city;
      var phone = orders[k].phone;
      var zipCode = orders[k].zipCode;
      var address = orders[k].address;
      var li = <Order items={items} firstName={firstName} lastName={lastName} email={email} city={city} phone={phone} zipCode={zipCode} address={address} />;
      console.log(li);
      ReactDOM.render(
        li,
        document.getElementByClass("o" + i)
      );
    }


    console.log(dlugosc)
  }

  function errData(err) {
    console.log('Error!');
    console.log(err);
  }

  function ret() {
    var container = [];
    for (var i = 0; i < dlugosc; i++) {
      container[i] = <Text class={'o' + i}>{i}</Text>;
    }
    return container
  }

  return (
    <SafeAreaView style={styles.app}>
      <View nativeID='o'>{dtb()}
        <Text>{ret()}</Text>
      </View>
    </SafeAreaView>
  )
}

export default App

const styles = StyleSheet.create({
  app: {
    backgroundColor: 'gray',
  }
})
