import React, { Component } from 'react'
import { FlatList, SafeAreaView, StyleSheet, Text, View } from 'react-native'
//import database from '@react-native-firebase/database';
import * as firebase from "firebase";
import { firebaseConfig } from "./Firebase";
import Order from "./Order";
//import { render } from 'react-dom';


if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

export default class Fetch extends Component {

  // const [dlugosc, setDlugosc] = useState();
  // const [orders, setOrders] = useState(0);
  // const [keys, setKeys] = useState(0);
  constructor(props) {
    super(props);
    this.state = {
      list: [],
    }
  }


  componentDidMount() {

    firebase.database()
      .ref('orders')
      .on('value', (snapshot) => {
        var li = [];
        snapshot.forEach((child) => {
          li.push({
            key: child.key,
            address: child.val().firstName,
            city: child.val().city,
            email: child.val().email,
            firstName: child.val().firstName,
            items: child.val().items,
            lastName: child.val().lastName,
            phone: child.val().phone,
            zipCode: child.val().zipCode,
          })
        })
        this.setState({ list: li })
      })
  }
  render() {
    return (
      <SafeAreaView style={styles.app}>
        <FlatList style={{ width: '100%' }}
          data={this.state.list}
          keyExtractor={(item) => item.key}
          renderItem={({ item }) => {
            return (
              <View style={styles.card}>
                <Text style={style.txt}>{item.items}</Text>
                <Text style={style.txt}>{item.firstName}</Text>
                <Text style={style.txt}>{item.lastName}</Text>
                <Text style={style.txt}>{item.email}</Text>
                <Text style={style.txt}>{item.city}</Text>
                <Text style={style.txt}>{item.address}</Text>
                <Text style={style.txt}>{item.phone}</Text>
                <Text style={style.txt}>{item.zipCode}</Text>
              </View>
            )
          }}
        />
      </SafeAreaView>
    )
  }
}


const styles = StyleSheet.create({
  app: {
    backgroundColor: 'gray',
  },
  card: {
    backgroundColor: 'black',
  }
})
